import React, {useState, useRef} from 'react';

import emailjs from 'emailjs-com';



const ContactForm = ({children}) => {

  const nameRef = useRef(null)
  const emailRef = useRef(null)
  const messageRef =  useRef(null)


  const handleSubmit = (e) => {
    e.preventDefault();

    const data = {
      name: nameRef.current.value,
      email: emailRef.current.value,
      message: messageRef.current.value,
    }
    emailjs.sendForm(
      'service_7ugyzgd',
      'template_u8jvcfm',
      JSON.stringify(data),
      'CNPOcX_lOqJC8MPax')
        .then(result => {
          console.log(result)
        }).catch(err => console.log(err))
    }

  return (
    <>
<form  className='contact'action="submit">
    <label htmlFor="name">Name</label>
    <input type="text" id='name'  />
       <label htmlFor="name" >E-mail address</label>
       <input type="text"  id='email' ref={nameRef} />
       <label htmlFor="name"  name='message' ref={emaillRef}>Message</label>
    <input type="text" id='message' ref={messageRef}/>
    <button action='submit' onClick={handleSubmit}>Submit</button>
</form>

<div className='hidden'></div>
</>
  );
};

export default ContactForm;
